import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { TaskService } from 'src/app/services/task.service';

@Component({
  selector: 'app-edit-task',
  templateUrl: './edit-task.component.html',
  styleUrls: ['./edit-task.component.css']
})
export class EditTaskComponent implements OnInit{

  selectedTask: any = {
    id: null,
    title: '',
    description: '',
    status: '',
    date: ''
  };

  constructor(
    private service: TaskService,
    private router: Router,
    private activeRoute: ActivatedRoute
  ) { }

  ngOnInit(): void {
    // Extract the task ID from the route parameter
    const id = this.activeRoute.snapshot.paramMap.get('id');
    this.getTask(id);
  }

  // Fetch the task details based on ID
  getTask(id: any) {
    this.service.getTaskById(id).subscribe((response) => {
      this.selectedTask = response;  // Assign the fetched data to selectedTask
    }, (error) => {
      console.error('Error fetching task', error);
    });
  }

  // Update task using the service
  updateTask() {
    this.service.updateTask(this.selectedTask).subscribe((response) => {
      console.log('Task updated successfully', response);

      // Navigate to the task list view page after the update
      this.router.navigate(['/home']);  // '/tasks' should be the route of your task list view page
    }, (error) => {
      console.error('Error updating task', error);
    });
  }
}
