import { Component } from '@angular/core';

@Component({
  selector: 'app-view-task',
  templateUrl: './view-task.component.html',
  styleUrls: ['./view-task.component.css']
})
export class ViewTaskComponent implements ngOn{

  tasks = [
    { id: 1, title: 'Task 1', description: 'Complete module 1', status: 'Pending', date: new Date() },
    { id: 2, title: 'Task 2', description: 'Fix bugs', status: 'In Progress', date: new Date() },
    { id: 3, title: 'Task 3', description: 'Deploy the app', status: 'Completed', date: new Date() }
  ];

  constructor() {}

  ngOnInit(): void {}

  editTask(id: number) {
    console.log('Edit task with ID:', id);
    // Add logic to edit task
  }

  deleteTask(id: number) {
    console.log('Delete task with ID:', id);
    // Add logic to delete task
  }

}
