import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class TaskService {

  constructor(private http:HttpClient) { }

  // private url = 'http://localhost:8081/tasks/add-task';

  private baseUrl = 'http://localhost:8081/tasks';

  course={
    id:0,
    title:'',
    description:''
  }

  addTask(id: any,title: any,description: any,
    status: any,date: any):Observable<any>{
      return this.http.post(this.baseUrl,{id,title,description,status,date})
  }

  getAllTask(): Observable<any> {
    return this.http.get(this.baseUrl);
  }
  getTaskById(id: any): Observable<any> {
    return this.http.get(`${this.baseUrl}/get-task-by-id/${id}`);
  }

  updateTask(task: any): Observable<any> {
    return this.http.put(`${this.baseUrl}/update-task`,task);
  }
  deleteTask(id: number): Observable<any> {
    return this.http.delete(`${this.baseUrl}/delete-task/${id}`);
  }
}
