import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { SignupService } from 'src/app/services/signup.service';
import { Router } from '@angular/router';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {
  signupForm: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private service: SignupService,
    private router: Router
  ) {
    this.signupForm = this.formBuilder.group({
      fullName: ['', Validators.required],
      username: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      phone: ['', [Validators.required, Validators.pattern('[0-9]{10}')]],
      password: ['', Validators.required],
      gender: ['', Validators.required]
    });
  }

  addUser() {
    if (this.signupForm.valid) {
      this.service.addUser(
        this.signupForm.value.fullName,
        this.signupForm.value.username,
        this.signupForm.value.email,
        this.signupForm.value.phone,
        this.signupForm.value.password,
        this.signupForm.value.gender
      ).subscribe(
        (response) => {
          console.log('User added successfully:', response);
          this.router.navigate(['/home']);
        },
        (error: HttpErrorResponse) => {
          if (error.status === 409) { // Assuming 409 for conflict
            alert('Username already exists. Please choose another one.');
          } else {
            console.error('Error occurred while adding user:', error.message);
          }
        }
      );
    } else {
      alert('Please fill all required fields correctly!');
    }
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Sign-Up Form</title>
  <link href="https://fonts.googleapis.com/css2?family=Poppins:wght@300;500;600&display=swap" rel="stylesheet">
  <style>
    .error {
      color: red;
      font-size: 0.8em;
    }
    .container {
      max-width: 500px;
      margin: auto;
      padding: 20px;
      border: 1px solid #ccc;
      border-radius: 10px;
    }
  </style>
</head>
<body>
  <div class="container">
    <h3>Sign Up Here</h3>
    <form [formGroup]="signupForm" (ngSubmit)="addUser()">
      <label for="fullName">Full Name</label>
      <input type="text" id="fullName" formControlName="fullName" placeholder="Enter your full name" required>
      <div *ngIf="signupForm.get('fullName')?.invalid && signupForm.get('fullName')?.touched" class="error">
        Full Name is required.
      </div>

      <label for="username">Username</label>
      <input type="text" id="username" formControlName="username" placeholder="Choose a username" required>
      <div *ngIf="signupForm.get('username')?.invalid && signupForm.get('username')?.touched" class="error">
        Username is required.
      </div>

      <label for="email">Email</label>
      <input type="email" id="email" formControlName="email" placeholder="Enter your email" required>
      <div *ngIf="signupForm.get('email')?.invalid && signupForm.get('email')?.touched" class="error">
        A valid email is required.
      </div>

      <label for="phone">Phone Number</label>
      <input type="tel" id="phone" formControlName="phone" placeholder="Enter your phone number" required>
      <div *ngIf="signupForm.get('phone')?.invalid && signupForm.get('phone')?.touched" class="error">
        Phone number is required and must be 10 digits.
      </div>

      <label for="password">Password</label>
      <input type="password" id="password" formControlName="password" placeholder="Create a password" required>
      <div *ngIf="signupForm.get('password')?.invalid && signupForm.get('password')?.touched" class="error">
        Password is required.
      </div>

      <label for="gender">Gender*</label>
      <select id="gender" formControlName="gender" required>
        <option value="" disabled selected>Select Your Gender</option>
        <option value="Male">Male</option>
        <option value="Female">Female</option>
      </select>
      <div *ngIf="signupForm.get('gender')?.invalid && signupForm.get('gender')?.touched" class="error">
        Gender is required.
      </div>

      <input type="submit" value="Sign Up" [disabled]="signupForm.invalid">
    </form>
  </div>
</body>
</html>
