import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LoginService } from 'src/app/services/login.service';

@Component({
  selector: 'app-forget-password',
  templateUrl: './forget-password.component.html',
  styleUrls: ['./forget-password.component.css']
})
export class ForgetPasswordComponent {


  username: string = '';
  newPassword: string = '';
  confirmPassword: string = '';

  constructor(private service: LoginService, private router: Routerr) {}

  changePassword() {
    if (this.newPassword !== this.confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    this.passwordResetService.changePassword(this.username, this.newPassword).subscribe(
      (response) => {
        alert("Password updated successfully.");
        this.router.navigate(['/login']); // Redirect to the login page after successful change
      },
      (error) => {
        if (error.status === 404) {
          alert("User not found.");
        } else {
          alert("An error occurred. Please try again.");
        }
      }
    );
  }
}
