import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { SignupService } from 'src/app/services/signup.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {

  constructor(private service:SignupService,private router:Router){}
fullName: any;
username: any;
email: any;
phone: any;
password: any;
gender: any;

addUser() {
  this.service.addUser(this.fullName, this.username, this.email, this.phone, this.password, this.gender)
    .subscribe(
      (response) => {
        console.log('User added successfully:', response);
        this.router.navigate(['/home']);
      },
      (error: HttpErrorResponse) => {
        if (error.status === 409) {
          // Handle conflict errors (username or email already exists)
          alert(error.error); // Display the message returned by the backend
        } else {
          console.error('Error occurred while adding user:', error.message);
          alert('An error occurred during registration. Please try again.');
        }
      }
    );
}

}
